var verifyToken = function verifyToken(req, res, next) {
  var authHeader = req.headers.token;
  if (authHeader) {
    var token = authHeader.split(" ")[1];
    _jsonwebtoken["default"].verify(token, process.env.JWT_KEY, function (err, user) {
      err && res.status(401).json("Token không hợp lệ!!!");
      req.user = user;
      next();
    });
  }
};
var verifyTokenRoleAdmin = function verifyTokenRoleAdmin(req, res, next) {
  verifyToken(req, res, function () {
    if (req.user.role == "admin") {
      next();
    } else {
      res.status(403).json("Bạn chưa được cấp quyền thực hiện thao tác này!");
    }
  });
};
var verifyTokenAndAuthorization = function verifyTokenAndAuthorization(req, res, next) {
  verifyToken(req, res, function () {
    if (req.user.id === req.params.id || req.user.role === "admin") {
